public abstract class Maze {
private final List<Room> rooms = new ArrayList<>();
public Maze() {
Room room1 = makeRoom();
Room room2 = makeRoom();
room1.connect(room2);
rooms.add(room1);
rooms.add(room2);
}
protected abstract Room makeRoom();
}
public class MagicMaze extends Maze {
@Override
protected Room makeRoom() {
return new MagicRoom();
}
}
